import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { FaCheck, FaTrash, FaPlus } from 'react-icons/fa';

const TaskProgress = ({ tasks = [] }) => {
  const { completedCount, totalCount, progressPercentage } = React.useMemo(() => {
    if (!Array.isArray(tasks)) return { completedCount: 0, totalCount: 0, progressPercentage: 0 };

    const completed = tasks.filter(task => task?.status).length;
    const total = tasks.length;
    const percentage = total === 0 ? 0 : Math.round((completed / total) * 100);

    return {
      completedCount: completed,
      totalCount: total,
      progressPercentage: percentage
    };
  }, [tasks]);

  return (
    <div className="mb-8 p-6 bg-white rounded-lg shadow-md">
      <div className="flex justify-between items-center mb-2">
        <h2 className="text-lg font-semibold text-gray-700">Task Progress</h2>
        <span className="text-sm font-medium text-indigo-600">
          {progressPercentage}% Complete
        </span>
      </div>
      <div className="w-full h-4 bg-gray-200 rounded-full overflow-hidden">
        <div 
          className="h-full bg-gradient-to-r from-indigo-500 to-purple-600 transition-all duration-500 ease-in-out"
          style={{ width: `${progressPercentage}%` }}
        />
      </div>
      <div className="mt-2 flex justify-between text-sm text-gray-500">
        <span>{completedCount} Completed</span>
        <span>{totalCount} Total Tasks</span>
      </div>
    </div>
  );
};

export default function TaskTracker() {
  const [tasks, setTasks] = useState([]);
  const [taskForm, setTaskForm] = useState({ name: '', deadline: '', status: false, eventName: '' });
  const [editingTaskId, setEditingTaskId] = useState(null);
  const [events, setEvents] = useState([]);

  // Fetch tasks from the backend
  const fetchTasks = async () => {
    try {
      const response = await axios.get('http://localhost:5001/api/getTasks');
      setTasks(response.data);
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  };

  // Fetch events from the backend
  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const response = await axios.get('http://localhost:5001/api/getEvents');
        setEvents(response.data);
      } catch (error) {
        console.error('Error fetching events:', error);
      }
    };

    fetchEvents();
  }, []);

  // Add a new task
  const addTask = async (e) => {
    e.preventDefault();
    try {
      console.log('Task Form Data:', taskForm);
      await axios.post('http://localhost:5001/api/postTask', taskForm);
      setTaskForm({ name: '', deadline: '', status: false, eventName: '' });
      fetchTasks();
      console.log('Task added successfully!');
    } catch (error) {
      console.error('Error adding task:', error.response?.data || error.message);
    }
  };

  // Update an existing task's status
  const updateTask = async (name) => {
    try {
      console.log('Updating Task:', name);
      await axios.put('http://localhost:5001/api/changeTaskStatus', { name });
      setEditingTaskId(null);
      fetchTasks();
    } catch (error) {
      console.error('Error updating task:', error);
    }
  };

  // Delete a task
  const deleteTask = async (name) => {
    try {
      console.log('Deleting Task:', name);
      await axios.delete('http://localhost:5001/api/deleteTask', { data: { name } });
      fetchTasks();
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setTaskForm({
      ...taskForm,
      [name]: type === 'checkbox' ? checked : value,
    });
  };

  // Load tasks on component mount
  useEffect(() => {
    fetchTasks();
  }, []);

  return (
    <div className="dashboard-container">
      <h1>Task Management Dashboard</h1>

      {/* Task Progress Component */}
      <TaskProgress tasks={tasks} />

      {/* Task Form */}
      <form className="task-form" onSubmit={editingTaskId ? updateTask : addTask}>
        <input
          type="text"
          name="name"
          placeholder="Task Name"
          value={taskForm.name}
          onChange={handleInputChange}
          required
        />
        <input
          type="date"
          name="deadline"
          value={taskForm.deadline}
          onChange={handleInputChange}
        />
        <select
          name="eventName"
          value={taskForm.eventName}
          onChange={handleInputChange}
          required
        >
          <option value="" disabled>Select an event</option>
          {events.map((event) => (
            <option key={event._id} value={event.name}>
              {event.name}
            </option>
          ))}
        </select>
        <button type="submit">
          <FaPlus /> Add Task
        </button>
      </form>

      {/* Task Table */}
      <div className="task-table">
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Deadline</th>
              <th>Status</th>
              <th>Event</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {tasks.map((task) => (
              <tr key={task._id}>
                <td>{task.name}</td>
                <td>{task.deadline ? new Date(task.deadline).toLocaleDateString() : 'No deadline'}</td>
                <td>{task.status ? 'Completed' : 'Pending'}</td>
                <td>{task.event || 'Unlinked'}</td>
                <td>
                  <button
                    className="update-button"
                    onClick={(e) => {
                      e.preventDefault();
                      updateTask(task.name);
                    }}
                  >
                    <FaCheck /> Update Status
                  </button>
                  <button
                    className="delete-button"
                    onClick={() => deleteTask(task.name)}
                  >
                    <FaTrash /> Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
