import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../styles/taskDashboard.css';

export default function TaskTracker() {
  const [tasks, setTasks] = useState([]);
  const [taskForm, setTaskForm] = useState({ name: '', deadline: '', status: false, event: '' });
  const [editingTaskId, setEditingTaskId] = useState(null);

  // Fetch tasks from the backend
  const fetchTasks = async () => {
    try {
      const response = await axios.get('http://localhost:5001/api/getTasks');
      setTasks(response.data);
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  };

  const [events, setEvents] = useState([]);

  // Fetch events from the API when the component mounts
  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const response = await axios.get('http://localhost:5001/api/getEvents');
        setEvents(response.data); // Assume the API returns an array of events
      } catch (error) {
        console.error('Error fetching events:', error);
      }
    };

    fetchEvents();
  }, []);

  // Add a new task
  const addTask = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5001/api/postTask', taskForm);
      setTaskForm({ name: '', deadline: '', status: false, eventName: '' });
      fetchTasks();
    } catch (error) {
      console.error('Error adding task:', error);
    }
  };

  // Update an existing task
  const updateTask = async (name) => {
    try {

      console.log(name);
      await axios.put(`http://localhost:5001/api/changeTaskStatus`, {name:name});
      setEditingTaskId(null);
      fetchTasks();
    } catch (error) {
      console.error('Error updating task:', error);
    }
  };

  // Delete a task
  const deleteTask = async (name) => {
    try {
      console.log(name);
      await axios.delete(`http://localhost:5001/api/deleteTask`,{data:{name:name}});
      fetchTasks();
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  // Handle form input change
  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    console.log(name);
    setTaskForm({
      ...taskForm,
      [name]: type === 'checkbox' ? checked : value,
    });
  };

  // Load tasks on component mount
  useEffect(() => {
    fetchTasks();
  }, []);

  return (
    <div className="dashboard-container">
      <h1>Task Management Dashboard</h1>

      <form className="task-form" onSubmit={editingTaskId ? updateTask : addTask}>
        <input
          type="text"
          name="name"
          placeholder="Task Name"
          value={taskForm.name}
          onChange={handleInputChange}
          required
        />
        <input
          type="date"
          name="deadline"
          value={taskForm.deadline}
          onChange={handleInputChange}
        />

        <select
              name="eventName"
              value={taskForm.eventName}
              onChange={handleInputChange}
            >
              <option value="" disabled>Select an event</option>
              {events.map((event) => (
                <option key={event._id} value={event.name}>
                  {event.name}
                </option>
              ))}
            </select>
        <button type="submit">Add Task</button>
      </form>

        <div className="task-table">
            <table>
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Deadline</th>
                  <th>Status</th>
                  <th>Event</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {tasks.map((task) => (
                  <tr key={task._id}>
                    <td>{task.name}</td>
                    <td>{task.deadline ? new Date(task.deadline).toLocaleDateString() : 'No deadline'}</td>
                    <td>{task.status ? 'Completed' : 'Pending'}</td>
                    <td>{task.event || 'Unlinked'}</td>
                    <td>
                      <button 
                        onClick={(e) => { 
                          e.preventDefault(); 
                          updateTask(task.name);
                        }}
                      >
                        Update Status
                      </button>
                      <button onClick={() => deleteTask(task.name)}>Delete</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
      </div>

    </div>
  );
}
