import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/Event.css';

export default function Event() {
  const [events, setEvents] = useState([]);
  const [newEvent, setNewEvent] = useState({
    name: '',
    date: '',
    location: '',
    description: '',
  });
  const [editingEvent, setEditingEvent] = useState(null);
  const [editForm, setEditForm] = useState({
    name: '',
    date: '',
    location: '',
    description: '',
  });

  useEffect(() => {
    fetchEvents();
  }, []);

  const fetchEvents = async () => {
    try {
      const response = await axios.get('http://localhost:5001/api/getEvents');
      setEvents(response.data);
    } catch (error) {
      console.error('Error fetching events:', error);
    }
  };

  const addEvent = async () => {
    try {
      if (!newEvent.name || !newEvent.date || !newEvent.location || !newEvent.description) {
        alert('All fields are required!');
        return;
      }
      await axios.post('http://localhost:5001/api/postEvent', newEvent);
      fetchEvents();
      setNewEvent({ name: '', date: '', location: '', description: '' });
    } catch (error) {
      console.error('Error adding event:', error);
    }
  };

  const deleteEvent = async (eventName) => {
    try {
      await axios.delete('http://localhost:5001/api/deleteEvent', {
        data: { name: eventName },
      });
      fetchEvents();
    } catch (error) {
      console.error('Error deleting event:', error);
    }
  };

  const startEditing = (event) => {
    setEditingEvent(event._id);
    setEditForm({
      name: event.name,
      date: event.date,
      location: event.location,
      description: event.description,
    });
  };

  const cancelEditing = () => {
    setEditingEvent(null);
    setEditForm({
      name: '',
      date: '',
      location: '',
      description: '',
    });
  };

  const updateEvent = async () => {
    try {
      await axios.put(`http://localhost:5001/api/updateEvent/${editingEvent}`, editForm);
      fetchEvents();
      cancelEditing();
    } catch (error) {
      console.error('Error updating event:', error);
    }
  };

  return (
    <div className="event-page">
      <h1>Event Management</h1>

      <div className="add-event-form">
        <h2>Add New Event</h2>
        <input
          type="text"
          placeholder="Event Name"
          value={newEvent.name}
          onChange={(e) => setNewEvent({ ...newEvent, name: e.target.value })}
        />
        <input
          type="date"
          value={newEvent.date}
          onChange={(e) => setNewEvent({ ...newEvent, date: e.target.value })}
        />
        <input
          type="text"
          placeholder="Location"
          value={newEvent.location}
          onChange={(e) => setNewEvent({ ...newEvent, location: e.target.value })}
        />
        <textarea
          placeholder="Description"
          value={newEvent.description}
          onChange={(e) => setNewEvent({ ...newEvent, description: e.target.value })}
        ></textarea>
        <button onClick={addEvent}>Add Event</button>
      </div>

      <div className="event-list">
        {events.map((event) => (
          <div key={event._id} className="event-row">
            {editingEvent === event._id ? (
              <div className="edit-form">
                <input
                  type="text"
                  value={editForm.name}
                  onChange={(e) => setEditForm({ ...editForm, name: e.target.value })}
                />
                <input
                  type="date"
                  value={editForm.date}
                  onChange={(e) => setEditForm({ ...editForm, date: e.target.value })}
                />
                <input
                  type="text"
                  value={editForm.location}
                  onChange={(e) => setEditForm({ ...editForm, location: e.target.value })}
                />
                <textarea
                  value={editForm.description}
                  onChange={(e) => setEditForm({ ...editForm, description: e.target.value })}
                ></textarea>
                <button onClick={updateEvent}>Save</button>
                <button onClick={cancelEditing}>Cancel</button>
              </div>
            ) : (
              <>
                <div className="event-details">
                  <h3>{event.name}</h3>
                  <p><strong>Description:</strong> {event.description}</p>
                  <p><strong>Location:</strong> {event.location}</p>
                  <p><strong>Date:</strong> {new Date(event.date).toLocaleDateString()}</p>
                </div>
                <div className="event-actions">
                  <button onClick={() => startEditing(event)}>Edit</button>
                  <button onClick={() => deleteEvent(event.name)}>Delete</button>
                </div>
              </>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}
