import './App.css';
import { BrowserRouter, Route, Routes } from 'react-router-dom';
import { useState } from 'react';
import Attendees from './Pages/Attendees';
import TaskTracker from './Pages/TaskTracker';
import Event from './Pages/Event';
import Sidebar from './Pages/Sidebar';
import Home from './Pages/Home';
import Login from './Pages/Login';
import Signup from './Pages/Signup';
import Dashboard from './Pages/Dashboard';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false); // Authentication state

  return (
    <div className="App">
      <BrowserRouter>
        <div className="app-layout">
          {/* Sidebar only for authenticated users */}
          {isAuthenticated && <Sidebar />}

          {/* Main Content Area */}
          <div className="main-content">
            <Routes>
              <Route path="/" element={<Home />} />
              <Route path="/login" element={<Login setIsAuthenticated={setIsAuthenticated} />} />
              <Route path="/signup" element={<Signup setIsAuthenticated={setIsAuthenticated} />} />
              <Route 
                path="/dashboard" 
                element={isAuthenticated ? <Dashboard /> : <Home />} 
              />
              <Route path="/event" element={isAuthenticated ? <Event /> : <Home />} />
              <Route path="/tasktracker" element={isAuthenticated ? <TaskTracker /> : <Home />} />
              <Route path="/attendees" element={isAuthenticated ? <Attendees /> : <Home />} />
            </Routes>
          </div>
        </div>
      </BrowserRouter>
    </div>
  );
}

export default App;
