import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import '../styles/sidebar.css';

const Sidebar = () => {
  const [isOpen, setIsOpen] = useState(false);
  const navigate = useNavigate();

  const toggleSidebar = () => {
    setIsOpen((prev) => !prev);
  };

  const handleLogout = () => {
    // Clear all auth-related data
    localStorage.clear(); // This will remove both token and user

    // Force a page reload and redirect
    window.location.replace('/login');

    // Alternative approach using navigate (if the above doesn't work)
    // navigate('/login', { replace: true });
  };

  const menuItems = [
    { text: 'Home', path: '/' },
    { text: 'Event', path: '/event' },
    { text: 'Task Tracker', path: '/tasktracker' },
    { text: 'Attendees', path: '/attendees' },
  ];

  return (
    <div className="sidebar-container">
      <div className={`sidebar ${isOpen ? 'open' : ''}`}>
        <button className="toggle-button" onClick={toggleSidebar}>
          â˜°
        </button>
        <ul className={`menu ${isOpen ? '' : 'hidden'}`}>
          {menuItems.map((item) => (
            <li key={item.text} className="menu-item">
              <Link to={item.path}>{item.text}</Link>
            </li>
          ))}
          <li className="menu-item">
            <button 
              onClick={handleLogout}
              className="logout-button"
            >
              Logout
            </button>
          </li>
        </ul>
      </div>
    </div>
  );
};

export default Sidebar;
