import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/Event.css';
import { FaEdit, FaSave, FaTrash } from 'react-icons/fa'; // Import icons

export default function Event() {
  const [events, setEvents] = useState([]);
  const [tasks, setTasks] = useState({});
  const [editingEventId, setEditingEventId] = useState(null);
  const [editForm, setEditForm] = useState({
    name: '',
    date: '',
    location: '',
    description: '',
  });
  const [newEvent, setNewEvent] = useState({
    name: '',
    date: '',
    location: '',
    description: '',
  });

  useEffect(() => {
    fetchEvents();
  }, []);

  const fetchEvents = async () => {
    try {
      const response = await axios.get('http://localhost:5001/api/getEvents');
      setEvents(response.data);
    } catch (error) {
      console.error('Error fetching events:', error);
    }
  };

  const fetchTasksForEvent = async (eventName) => {
    try {
      const response = await axios.get('http://localhost:5001/api/getEventTasks', {
        params: { name: eventName },
      });
      setTasks((prevTasks) => ({
        ...prevTasks,
        [eventName]: response.data,
      }));
    } catch (error) {
      console.error('Error fetching tasks for event:', error);
    }
  };

  const toggleTasks = (eventName) => {
    if (tasks[eventName]) {
      setTasks((prevTasks) => {
        const updatedTasks = { ...prevTasks };
        delete updatedTasks[eventName];
        return updatedTasks;
      });
    } else {
      fetchTasksForEvent(eventName);
    }
  };

  const deleteTask = async (taskName) => {
    try {
      await axios.delete('http://localhost:5001/api/deleteTask', {
        data: { name: taskName },
      });
      fetchTasksForEvent(taskName);
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  const deleteEvent = async (eventName) => {
    try {
      await axios.delete('http://localhost:5001/api/deleteEvent', {
        data: { name: eventName },
      });
      fetchEvents();
    } catch (error) {
      console.error('Error deleting event:', error);
    }
  };

  const startEditing = (event) => {
    setEditingEventId(event._id);
    setEditForm({
      name: event.name,
      date: event.date,
      location: event.location,
      description: event.description,
    });
  };

  const cancelEditing = () => {
    setEditingEventId(null);
    setEditForm({
      name: '',
      date: '',
      location: '',
      description: '',
    });
  };

  const updateEvent = async () => {
    try {
      await axios.put(`http://localhost:5001/api/updateEvent/${editingEventId}`, editForm);
      fetchEvents();
      cancelEditing();
    } catch (error) {
      console.error('Error updating event:', error);
    }
  };

  const addEvent = async () => {
    try {
      if (!newEvent.name || !newEvent.date || !newEvent.location || !newEvent.description) {
        alert('All fields are required!');
        return;
      }
      await axios.post('http://localhost:5001/api/postEvent', newEvent);
      fetchEvents();
      setNewEvent({ name: '', date: '', location: '', description: '' });
    } catch (error) {
      console.error('Error adding event:', error);
    }
  };

  return (
    <div className="event-page">
      <h1>Event Management</h1>

      <div className="add-event-form">
        <h2>Add New Event</h2>
        <input
          type="text"
          placeholder="Event Name"
          value={newEvent.name}
          onChange={(e) => setNewEvent({ ...newEvent, name: e.target.value })}
        />
        <input
          type="date"
          value={newEvent.date}
          onChange={(e) => setNewEvent({ ...newEvent, date: e.target.value })}
        />
        <input
          type="text"
          placeholder="Location"
          value={newEvent.location}
          onChange={(e) => setNewEvent({ ...newEvent, location: e.target.value })}
        />
        <textarea
          placeholder="Description"
          value={newEvent.description}
          onChange={(e) => setNewEvent({ ...newEvent, description: e.target.value })}
        ></textarea>
        <button onClick={addEvent}>Add Event</button>
      </div>

      <div className="event-table">
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Date</th>
              <th>Location</th>
              <th>Description</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {events.map((event) => (
              <React.Fragment key={event._id}>
                <tr>
                  <td>
                    {editingEventId === event._id ? (
                      <input
                        type="text"
                        value={editForm.name}
                        onChange={(e) => setEditForm({ ...editForm, name: e.target.value })}
                      />
                    ) : (
                      event.name
                    )}
                  </td>
                  <td>
                    {editingEventId === event._id ? (
                      <input
                        type="date"
                        value={editForm.date}
                        onChange={(e) => setEditForm({ ...editForm, date: e.target.value })}
                      />
                    ) : (
                      new Date(event.date).toLocaleDateString()
                    )}
                  </td>
                  <td>
                    {editingEventId === event._id ? (
                      <input
                        type="text"
                        value={editForm.location}
                        onChange={(e) => setEditForm({ ...editForm, location: e.target.value })}
                      />
                    ) : (
                      event.location
                    )}
                  </td>
                  <td>
                    {editingEventId === event._id ? (
                      <textarea
                        value={editForm.description}
                        onChange={(e) => setEditForm({ ...editForm, description: e.target.value })}
                      />
                    ) : (
                      event.description
                    )}
                  </td>
                  <td>
                    {editingEventId === event._id ? (
                      <button onClick={updateEvent} className="save-button">
                        <FaSave /> Save
                      </button>
                    ) : (
                      <button onClick={() => startEditing(event)} className="edit-button">
                        <FaEdit /> Edit
                      </button>
                    )}
                    <button onClick={() => deleteEvent(event.name)} className="delete-button">
                      <FaTrash /> Delete
                    </button>
                  </td>
                </tr>
                {tasks[event.name] && (
                  <tr>
                    <td colSpan="5">
                      <table className="task-table">
                        <thead>
                          <tr>
                            <th>Task Name</th>
                            <th>Deadline</th>
                            <th>Status</th>
                            <th>Actions</th>
                          </tr>
                        </thead>
                        <tbody>
                          {tasks[event.name].map((task) => (
                            <tr key={task._id}>
                              <td>{task.name}</td>
                              <td>{new Date(task.deadline).toLocaleDateString()}</td>
                              <td>{task.status ? 'Completed' : 'Pending'}</td>
                              <td>
                                <button onClick={() => deleteTask(task.name)}>Delete Task</button>
                              </td>
                            </tr>
                          ))}
                        </tbody>
                      </table>
                    </td>
                  </tr>
                )}
              </React.Fragment>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
